package senhafactory;import javax.swing.JOptionPane;/** * @author 		Ermerson Rafael - 816122707 * @category	Aplicações e Serviços * @since		21/02/2018 * @version		v1.0.0.4 */public class Main {	public static void main(String[] args) {		String senha = JOptionPane.showInputDialog(null, "Digite a senha:");		if (senha.isEmpty()) {			System.out.println("Digite uma senha...");			System.exit(1);		}		ProvedorInformacao provedor = ("designpatterns".equals(senha)) ? new ProvedorConfidencial()				: new ProvedorPublico();		provedor.exibirInformacao();	}}